mapper.select:
SELECT

FROM user a

WHERE a.id = #{id}

SELECT

FROM user a


    a.user_type = #{USERTYPE_NORMAL}

        AND a.username like concat('%',#{username},'%')


        AND a.valid = #{valid}



    ORDER BY ${page.orderBy}

mapper.update:
UPDATE user SET
username = #{username},
password = #{password},
user_type = #{userType},
focus = #{focus}
WHERE id = #{id}

UPDATE user SET
valid = #{VALID_DISABLE}
WHERE id = #{id}

UPDATE user SET
valid = #{VALID_ENABLE}
WHERE id = #{id}

UPDATE user SET
valid = #{VALID_DELETE}
WHERE id = #{id}

SELECT

FROM user a

WHERE a.username = #{username}
